// src/components/KPIDiagram.jsx
import React, { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import ReactFlow, {
  Background,
  Controls,
  useNodesState,
  useEdgesState,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import axios from 'axios';

const CustomNode = ({ data }) => {
  return (
    <div
      style={{
        border: '1px solid #ccc',
        padding: '12px 16px',
        borderRadius: 12,
        background: '#fff',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        minWidth: 180,
        maxWidth: 200,
        textAlign: 'center',
        fontSize: 14,
        fontWeight: 500,
        position: 'relative',
      }}
    >
      <Handle type="target" position={Position.Top} id="a" />
      <div style={{ marginBottom: 8 }}>{data.label}</div>
      {data.hasChildren && (
        <button
          onClick={data.onDrill}
          style={{
            padding: '4px 8px',
            fontSize: 12,
            borderRadius: 6,
            border: 'none',
            background: '#007bff',
            color: 'white',
            cursor: 'pointer',
          }}
        >
          ➕
        </button>
      )}
      <Handle type="source" position={Position.Bottom} id="a" />
    </div>
  );
};


const KPIDiagram = () => {
  const [level, setLevel] = useState(0);
  const newNodesRef = useRef([]);
  const edgesRef = useRef([]);

  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  // Memoize nodeTypes to avoid React Flow warning
  const nodeTypes = useMemo(() => ({ customNode: CustomNode }), []);

  const fetchNode = async (nodeId) => {
    try {
      const res = await axios.get(`http://localhost:8000/api/node/${nodeId}`);
      // if(!res.data.found)
      //   return;
      console.log(res)
      return res.data.node;
    } catch (error) {
      console.error('Error fetching node:', error);
      return null;
    }
  };

  const handleDrill = useCallback(
    async (nodeId) => {
      const isExpanded = expandedNodeIds.has(nodeId);

      if (isExpanded) {
        // Collapse: remove all children nodes and their edges
        const updatedNodes = newNodesRef.current.filter(
          (n) => !(n.id !== nodeId && edgesRef.current.some(e => e.source === nodeId && e.target === n.id))
        );
        const updatedEdges = edgesRef.current.filter(e => e.source !== nodeId);

        // Update refs
        newNodesRef.current = updatedNodes;
        edgesRef.current = updatedEdges;

        setNodes([...updatedNodes]);
        setEdges([...updatedEdges]);

        // Remove from expanded set
        setExpandedNodeIds((prev) => {
          const newSet = new Set(prev);
          newSet.delete(nodeId);
          return newSet;
        });

        return;
      }

      // Expand: fetch and add children
      const node = await fetchNode(nodeId);
      if (!node || !node.childrenData) return;

      const nextLevel = parseInt(node.level) + 1 || level + 1;
      const entries = Object.entries(node.childrenData);
      const totalChildren = entries.length;
      const baseY = 100 + nextLevel * 180;

      const parentNode = newNodesRef.current.find((n) => n.id === nodeId);
      const centerX = parentNode?.position?.x || 0;
      const spacing = Math.max(200, 1200 / totalChildren);
      const startX = centerX - ((totalChildren - 1) * spacing) / 2;

      entries.forEach(([childId, childLabel], index) => {
        const childX = startX + index * spacing;

        edgesRef.current.push({
          id: `e-${nodeId}-${childId}`,
          source: nodeId,
          sourceHandle: 'a',
          target: childId,
          targetHandle: 'a',
          animated: true,
        });

        newNodesRef.current.push({
          id: childId,
          type: 'customNode',
          position: { x: childX, y: baseY },
          data: {
            label: childLabel.label,
            id: childId,
            hasChildren: childLabel.hasChildren,
            onDrill: () => {
              if (childLabel.hasChildren) handleDrill(childId);
            },
          },
        });
      });

      const uniqueNodes = Array.from(
        new Map(newNodesRef.current.map((n) => [n.id, n])).values()
      );
      const uniqueEdges = Array.from(
        new Map(edgesRef.current.map((e) => [e.id, e])).values()
      );

      setNodes(uniqueNodes);
      setEdges(uniqueEdges);
      setLevel(nextLevel);

      // Mark this node as expanded
      setExpandedNodeIds((prev) => new Set(prev).add(nodeId));
    },
    [expandedNodeIds, level]
  );


  useEffect(() => {
    const loadRoot = async () => {
      const root = await fetchNode('EC140');
      if (!root) return;

      // Use root's own _id or parentId? Usually root.id or _id
      const rootId = root.parentId || 'EC140';
      newNodesRef.current.push({
        id: rootId,
        type: 'customNode',
        position: { x: 500, y: 50 },
        data: {
          label: root.parentName || 'Root Node',
          id: rootId,
          hasChildren : true,
          onDrill: () => handleDrill(rootId),
        },
      });

      setNodes([...newNodesRef.current]);
    };
    loadRoot();
  }, [handleDrill]);

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            nodeTypes={nodeTypes}
            fitView
            fitViewOptions={{ padding: 0.4 }} // zoom and center nicely
          >
        <Background />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default KPIDiagram;
