// src/components/KPIDiagram.jsx
import React, {useEffect,useRef, useMemo, useState, useCallback, Children} from 'react';
import ReactFlow, {
  Background,
  useNodesState,
  useEdgesState,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import axios from 'axios'
const initialNodes = [
  {
    id: 'EC140',
    type: 'customNode',
    position: { x: 250, y: 5 },
    data: {
      label: 'Autofinancement',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'EC140',
    },
  },
  {
    id: 'R07',
    type: 'customNode',
    position: { x: 100, y: 150 },
    data: {
      label: 'Capacité d\'autofinancement',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'R07',
    },
  },
  {
    id: 'EC047',
    type: 'customNode',
    position: { x: 400, y: 150 },
    data: {
      label: 'Dividendes',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'EC047',
    },
  },
];

const initialEdges = [
  { id: 'e1-2', source: 'EC140', target: 'R07', animated: true },
  { id: 'e1-3', source: 'EC140', target: 'EC047', animated: true },
];

const CustomNode = ({ data }) => (
  <div style={{
    border: '1px solid #ccc',
    borderRadius: 8,
    padding: 12,
    background: '#fff',
    minWidth: 180,
    position: 'relative',
  }}>
    <div style={{ fontWeight: 'bold', marginBottom: 4 }}>{data.label}</div>
    <button
      style={{
        position: 'absolute',
        right: 4,
        bottom: 4,
        fontSize: 12,
        background: '#eee',
        borderRadius: 4,
        padding: '2px 6px',
        cursor: 'pointer',
        border: 'none',
      }}
      onClick={() => data.onDrill(data.id)}
    >
      +
    </button>
    <Handle type="target" position={Position.Top} style={{ opacity: 0 }} />
    <Handle type="source" position={Position.Bottom} style={{ opacity: 0 }} />
  </div>
);

const nodeTypes = {
  customNode: CustomNode,
};

const KPIDiagram = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [level, SetLevel] = useState(1);
  const [position, setPosition] = useState({x : 150, y : 5});
  // const [nodesData, setNodesData] = useState(null);
  const newNodesRef = useRef([]);
  const edgesRef = useRef([]);

  useEffect(()=>{
    const fetch = async ()=>{
      try{
          const response = await axios.post("http://localhost:8000/api/getData",{ level :level});
          const nodesData  = response.data.nodesData;

          nodesData.forEach(node => {
            const parentId = node.parentId;
            Object.entries(node.childrenData).forEach((child, index )=>{
              edgesRef.current.push({id : `e-${parentId}-${child}`, source : parentId, target : child[0], animated : true} )

                }
              );
            // console.log(Edges);
            newNodesRef.current.push({
              id : node.parentId,
              type : "customNode",
              position : {x : position.x, y : position.y},
              data : {
                label : node.parentName,
                onDrill : () => {
                  setPosition((prev) => ({
                    ...prev,
                    x : prev.x + 150 *  Math.random(),
                  }))

                  Object.entries(node.childrenData).forEach(([childId, childLabel], index) => {
                    edgesRef.current.push({id: `e-${parentId}-${childId}`, source : parentId, target : childId, animated : true} )
                    newNodesRef.current.push({
                      id: childId,
                      type: "customNode",
                      position: {
                        x: position.x + 150 *  Math.random(),
                        y: position.y + 150 * Math.random()
                      },
                      data: {
                        label: childLabel,
                        onDrill: () => alert("Drill into node " + childId),
                        id: childId,
                      }
                    });
                  });
                  setPosition((prev) => ({
                    ...prev,
                    xy : prev.y + 100 *  Math.random(),
                  }))

                  SetLevel(prev => prev + 1);
                }
              }
            });
            setPosition((prev) => ({
              ...prev,
              y : prev.y + 50 *  Math.random(),
            }))
          });

          const uniqueNodes = Array.from(
            new Map(newNodesRef.current.map(node => [node.id, node])).values()
          );
          setNodes(uniqueNodes);
          setEdges(edgesRef.current)
          console.log(d.current);
          setPosition((prev) => ({
            ...prev,
            xy : prev.y + 100,
          }))
      }
      catch(error)
      {
        console.error(error);
      }
    }

    fetch();
  }, [level])

  return (
    <div style={{ height: '80vh', width: '100%' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        nodeTypes={nodeTypes}
        onNodesChange={onNodesChange} 
        onEdgesChange={onEdgesChange}
        fitView
        panOnDrag={false}
        zoomOnScroll={true}
        nodesDraggable={true}
        nodesConnectable={true}
      >
        {/* <Background /> */}
      </ReactFlow>
    <button onClick={()=>{SetLevel(prev => prev + 1)}}>press</button>
    </div>
  );
};

export default KPIDiagram;
