import React, { useState, useRef, useEffect, memo } from "react";
import { motion } from "framer-motion";
import { Handle, Position, ReactFlow, Background } from "reactflow";
import "reactflow/dist/style.css";

// --- Collapsible field component ---
const CollapsibleField = ({ label, value }) => {
  const [open, setOpen] = useState(false);
  const isObject = typeof value === "object" && value !== null;

  return (
    <div className="mb-1 border-b border-gray-100 pb-1">
      <div
        className="flex items-center justify-between cursor-pointer text-xs font-semibold text-gray-600 hover:text-blue-600"
        onClick={() => isObject && setOpen(!open)}
      >
        <span>{label}:</span>
        {isObject && (
          <span className="text-[10px] bg-gray-200 rounded px-1">
            {open ? "Hide" : "Show"}
          </span>
        )}
      </div>

      {!isObject && (
        <div className="text-xs text-gray-800 break-words">{String(value)}</div>
      )}

      {isObject && open && (
        <pre className="bg-gray-100 p-1 rounded text-[10px] overflow-x-auto max-h-32 overflow-y-auto mt-1">
          {JSON.stringify(value, null, 2)}
        </pre>
      )}
    </div>
  );
};

// --- Custom node ---
const CustomNode = memo(({ data }) => {
  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.98 }}
      animate={{ opacity: 1, scale: 1 }}
      exit={{ opacity: 0, scale: 0.98 }}
      transition={{ duration: 0.2 }}
      className="relative w-[300px] bg-gradient-to-br from-white to-gray-50 border border-gray-200 rounded-xl p-4 shadow-md font-sans overflow-hidden"
    >
      <Handle type="target" position={Position.Left} className="opacity-0" />

      <div className="text-lg font-bold text-blue-600 mb-2">
        {data.label || data.parentId}
      </div>

      <div className="max-h-60 overflow-y-auto">
        {Object.entries(data).map(([key, value]) => (
          <CollapsibleField key={key} label={key} value={value} />
        ))}
      </div>

      {data.hasChildren && (
        <button
          onClick={data.onDrill}
          className="absolute bottom-2 right-2 text-sm bg-indigo-100 text-indigo-700 rounded px-2 py-0.5"
        >
          {data.expanded ? "−" : `${data.childrenNum}`}
        </button>
      )}

      <Handle type="source" position={Position.Right} className="opacity-0" />
    </motion.div>
  );
});

// --- Main component ---
export default function FlowWithCollapsibleNodes() {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const newNodesRef = useRef([]);

  useEffect(() => {
    loadRoot();
  }, []);

  // Simulate API calls
  const fetchRootFromAPI = async () => ({
    _id: "root-1",
    label: "Root Node",
    childrenNum: 2,
    childrenData: [
      { _id: "child-1", label: "Child One", childrenNum: 1 },
      { _id: "child-2", label: "Child Two", childrenNum: 0 },
    ],
  });

  const fetchChildrenFromAPI = async (parentId) => {
    if (parentId === "child-1") {
      return [
        { _id: "grandchild-1", label: "Grandchild", childrenNum: 0 },
      ];
    }
    return [];
  };

  const loadRoot = async () => {
    const root = await fetchRootFromAPI();
    const rootId = root._id;

    newNodesRef.current.push({
      id: rootId,
      type: "customNode",
      position: { x: 0, y: 0 },
      data: {
        ...root,
        id: rootId,
        hasChildren: true,
        expanded: false,
        onDrill: () => handleDrill(rootId),
      },
    });

    setNodes([...newNodesRef.current]);
  };

  const handleDrill = async (parentId) => {
    const parentNode = newNodesRef.current.find((n) => n.id === parentId);
    if (!parentNode || parentNode.expanded) return;

    const children = await fetchChildrenFromAPI(parentId);

    children.forEach((child, index) => {
      newNodesRef.current.push({
        id: child._id,
        type: "customNode",
        position: {
          x: parentNode.position.x + 400,
          y: parentNode.position.y + index * 150,
        },
        data: {
          ...child,
          id: child._id,
          hasChildren: !!child.childrenNum,
          expanded: false,
          onDrill: () => handleDrill(child._id),
        },
      });

      setEdges((eds) => [
        ...eds,
        { id: `${parentId}-${child._id}`, source: parentId, target: child._id },
      ]);
    });

    parentNode.data.expanded = true;
    setNodes([...newNodesRef.current]);
  };

  return (
    <div style={{ width: "100%", height: "100vh" }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        nodeTypes={{ customNode: CustomNode }}
        fitView
      >
        <Background />
      </ReactFlow>
    </div>
  );
}
