import React, { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import ReactFlow, {
  Background,
  Controls,
  useNodesState,
  useReactFlow, 
  useEdgesState,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import axios from 'axios';

// CustomNode component
import { motion, AnimatePresence } from 'framer-motion';
const CustomNode = ({ data }) => {
  return (
    <AnimatePresence>
      {!data.collapsing && (
        <motion.div
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.8 }}
          transition={{ duration: 0.4 }}
          style={{
            border: '1px solid #ccc',
            padding: '12px 16px',
            borderRadius: 12,
            background: '#fff',
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            minWidth: 180,
            maxWidth: 200,
            textAlign: 'center',
            fontSize: 14,
            fontWeight: 500,
            position: 'relative',
          }}
          layout
          key={data.id}
        >
          <Handle type="target" position={Position.Top} id="a" />
          <div style={{ marginBottom: 8 }}>{data.label}</div>
          {data.hasChildren && (
            <button
              onClick={data.onDrill}
              style={{
                padding: '4px 8px',
                fontSize: 12,
                borderRadius: 6,
                border: 'none',
                background: '#007bff',
                color: 'white',
                cursor: 'pointer',
              }}
            >
              {data.expanded ? '➖' : '➕'}
            </button>
          )}
          <Handle type="source" position={Position.Bottom} id="a" />
        </motion.div>
      )}
    </AnimatePresence>
  );
};


const KPIDiagram = () => {
  const [level, setLevel] = useState(0);
  const newNodesRef = useRef([]);
  const edgesRef = useRef([]);
  const expandedNodesRef = useRef(new Set()); // Track expanded nodes
  const reactFlowWrapper = useRef(null);
  const reactFlowInstance = useRef(null); 

  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  const nodeTypes = useMemo(() => ({ customNode: CustomNode }), []);

  const fetchNode = async (nodeId) => {
    try {
      const res = await axios.get(`http://localhost:8000/api/node/${nodeId}`);
      return res.data.node;
    } catch (error) {
      console.error('Error fetching node:', error);
      return null;
    }
  };

  const removeDescendants = (parentId) => {
    const toRemove = new Set();
    const stack = [parentId];

    while (stack.length > 0) {
      const current = stack.pop();

      // Find all direct children of current node
      const children = edgesRef.current
        .filter(e => e.source === current)
        .map(e => e.target);

      for (const childId of children) {
        // Check if child has other parents besides `current`
        const otherParents = edgesRef.current.filter(
          e => e.target === childId && e.source !== current
        );

        if (otherParents.length === 0) {
          // No other parents, safe to remove this child and continue recursion
          stack.push(childId);
          toRemove.add(childId);
        }
        // else: child has other parents, so keep it
      }
    }

    // Remove all descendant nodes that are safe to remove
    newNodesRef.current = newNodesRef.current.filter(n => !toRemove.has(n.id));

    // Remove all edges where source or target is in toRemove
    edgesRef.current = edgesRef.current.filter(
      e => !toRemove.has(e.source) && !toRemove.has(e.target)
    );

    // Remove parent from expanded set
    expandedNodesRef.current.delete(parentId);

    // Mark the parent node as collapsed (expanded: false)
    newNodesRef.current = newNodesRef.current.map(n =>
      n.id === parentId ? { ...n, data: { ...n.data, expanded: false } } : n
    );
  };

  // Helper function to compute bounding box of nodes by id list
  const computeBounds = (nodes) => {
    if (nodes.length === 0) return null;
    let minX = nodes[0].position.x;
    let minY = nodes[0].position.y;
    let maxX = nodes[0].position.x;
    let maxY = nodes[0].position.y;

    nodes.forEach(({ position, style }) => {
      const width = (style?.width ?? 180); // default width if not specified
      const height = (style?.height ?? 80); // default height if not specified
      minX = Math.min(minX, position.x);
      minY = Math.min(minY, position.y);
      maxX = Math.max(maxX, position.x + width);
      maxY = Math.max(maxY, position.y + height);
    });

    return { x: minX, y: minY, width: maxX - minX, height: maxY - minY };
  };


  const handleDrill = useCallback(
    async (nodeId) => {
      const isExpanded = expandedNodesRef.current.has(nodeId);

      if (isExpanded) {
        removeDescendants(nodeId);

        setNodes([...newNodesRef.current]);
        setEdges([...edgesRef.current]);
        const subtreeNodeIds = [nodeId, ...entries.map(([childId]) => childId)];
        const subtreeNodes = newNodesRef.current.filter(n => subtreeNodeIds.includes(n.id));
        const bounds = computeBounds(subtreeNodes);

        if (bounds && reactFlowInstance.current) {
          reactFlowInstance.current.fitBounds(bounds, {
            padding: 0.4,
            maxZoom: 1.5,
          });
        }
        return;
      }

      // Expand
      const node = await fetchNode(nodeId);
      if (!node || !node.childrenData) return;

      const parentId = node.parentId || nodeId;
      const nextLevel = parseInt(node.level) + 1 || level + 1;
      const entries = Object.entries(node.childrenData);
      const totalChildren = entries.length;
      const baseY = 100 + nextLevel * 180;

      const parentNode = newNodesRef.current.find((n) => n.id === nodeId);
      const centerX = parentNode?.position?.x || 0;
      const spacing = Math.max(600, 1200 / totalChildren);
      const startX = centerX - ((totalChildren - 1) * spacing) / 2;

      entries.forEach(([childId, childLabel], index) => {
        const childX = startX + index * spacing;

        edgesRef.current.push({
          id: `e-${nodeId}-${childId}`,
          source: nodeId,
          sourceHandle: 'a',
          target: childId,
          targetHandle: 'a',
          animated: true,
        });

        newNodesRef.current.push({
          id: childId,
          type: 'customNode',
          position: { x: childX, y: baseY },
          data: {
            label: childLabel.label,
            id: childId,
            hasChildren: childLabel.hasChildren,
            expanded: false,
            onDrill: () => handleDrill(childId),
          },
        });
      });

      expandedNodesRef.current.add(nodeId);

      // Update parent node with `expanded: true`
      newNodesRef.current = newNodesRef.current.map(n =>
        n.id === nodeId
          ? {
              ...n,
              data: { ...n.data, expanded: true },
            }
          : n
      );

      // Deduplicate
      const uniqueNodes = Array.from(new Map(newNodesRef.current.map((n) => [n.id, n])).values());
      const uniqueEdges = Array.from(new Map(edgesRef.current.map((e) => [e.id, e])).values());

      setNodes(uniqueNodes);
      setEdges(uniqueEdges);
      setLevel(nextLevel);
      const subtreeNodeIds = [nodeId, ...entries.map(([childId]) => childId)];
      const subtreeNodes = newNodesRef.current.filter(n => subtreeNodeIds.includes(n.id));
      const bounds = computeBounds(subtreeNodes);

      if (bounds && reactFlowInstance.current) {
        reactFlowInstance.current.fitBounds(bounds, {
          padding: 0.4,
          maxZoom: 1.5,
        });
      }
    },
    [level]
  );
  const onInit = useCallback((rfi) => {
    reactFlowInstance.current = rfi;
  }, []);

  const fitViewWithDelay = () => {
    setTimeout(() => {
      if (reactFlowInstance.current) {
        reactFlowInstance.current.fitView({ padding: 0.2 });
      }
    }, 100);
  };

  useEffect(() => {
    const loadRoot = async () => {
      const root = await fetchNode('EC140');
      if (!root) return;

      const rootId = root.parentId || 'EC140';
      newNodesRef.current.push({
        id: rootId,
        type: 'customNode',
        position: { x: 500, y: 50 },
        data: {
          label: root.parentName || 'Root Node',
          id: rootId,
          hasChildren: true,
          expanded: false,
          onDrill: () => handleDrill(rootId),
        },
      });

      setNodes([...newNodesRef.current]);
    };
    loadRoot();
  }, [handleDrill]);

  return (
    <div style={{ width: '100vw', height: '100vh' }} ref={reactFlowWrapper}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
        fitViewOptions={{ padding: 0.4 }}
        onInit={onInit}
      >
        <Background />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default KPIDiagram;
