import React, { useCallback, useEffect, useMemo, useRef, useState , memo} from 'react';
import ReactFlow, {
  Background,
  Controls,
  useNodesState,
  useReactFlow, 
  useEdgesState,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import axios from 'axios';
import { motion } from 'framer-motion';

const CustomNode = memo(({ data }) => {
  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.98 }}
      animate={{ opacity: 1, scale: 1 }}
      exit={{ opacity: 0, scale: 0.98 }}
      transition={{ duration: 0.2 }}
      style={{
        backgroundColor: '#ffffff',
        border: '1px solid #e5e7eb',
        borderRadius: '12px',
        padding: '16px',
        width: 250,
        boxShadow: '0px 1px 4px rgba(0, 0, 0, 0.05)',
        fontFamily: 'Inter, sans-serif',
        position: 'relative',
      }}
    >
      <Handle type="target" position={Position.Left} style={{ opacity: 0 }} />
      <div style={{ fontSize: 12, fontWeight: 500, color: '#2563eb', marginBottom: 0 }}>
        nodeID : {data.id}
      </div>
      <div style={{ fontSize: 12, fontWeight: 500, color: '#2563eb', marginBottom: 4 }}>
        reportName : {data.reportName}
      </div>
      <div style={{ fontSize: 16, color: '#374151' }}>{data.label}</div>
      {data.hasChildren && (
        <button
          onClick={data.onDrill}
          style={{
            position: 'absolute',
            bottom: 8,
            right: 8,
            fontSize: 14,
            border: 'none',
            borderRadius: '4px',
            background: '#e0e7ff',
            color: '#1d4ed8',
            cursor: 'pointer',
            padding: '2px 6px'
          }}
        >
          {data.expanded ? 'âˆ’' : `${data.childrenNum}`}
        </button>
      )}
      <Handle type="source" position={Position.Right} style={{ opacity: 0 }} />
    </motion.div>
  );
});

const defaultEdgeOptions = {
  type: 'smoothstep',
};

const layoutHorizontalTree = (node, childrenMap, x = 0, y = 0, spacing = { x: 250, y: 100 }) => {
  const children = childrenMap[node.id] || [];

  let nodes = [];
  let edges = [];

  if (children.length === 0) {
    const currentNode = {
      id: node.id,
      type: 'customNode',
      position: { x, y },
      data: node.data,
    };
    return {
      nodes: [currentNode],
      edges: [],
      height: spacing.y,
      centerY: y,
    };
  }

  let totalHeight = 0;
  const childLayouts = [];

  for (const child of children) {
    const childLayout = layoutHorizontalTree(
      child,
      childrenMap,
      x + spacing.x,
      y + totalHeight,
      spacing
    );
    totalHeight += childLayout.height;
    childLayouts.push(childLayout);
  }

  const centerY = y + totalHeight / 2;

  const currentNode = {
    id: node.id,
    type: 'customNode',
    position: { x, y: centerY },
    data: node.data,
  };

  childLayouts.forEach((layout) => {
    edges.push({
      id: `e-${node.id}-${layout.nodes[0].id}`,
      source: node.id,
      target: layout.nodes[0].id,
      sourceHandle: 'a',
      targetHandle: 'a',
    });
  });

  return {
    nodes: [currentNode, ...childLayouts.flatMap((c) => c.nodes)],
    edges,
    height: totalHeight,
    centerY,
  };
};

const KPIDiagram = () => {
  const newNodesRef = useRef([]);
  const edgesRef = useRef([]);
  const reactFlowInstance = useRef(null);

  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  const nodeTypes = useMemo(() => ({ customNode: CustomNode }), []);

  const fetchNode = async (nodeId) => {
    try {
      const res = await axios.get(`http://localhost:8000/api/node/${nodeId}`);
      return res.data.node;
    } catch (error) {
      console.error('Error fetching node:', error);
      return null;
    }
  };

  const handleDrill = useCallback(async (nodeId) => {
    const node = await fetchNode(nodeId);
    if (!node || !node.childrenData) return;

    const rootNode = {
      id: node.id,
      data: {
        label: node.parentName || node.label,
        id: node.id,
        reportName: node.reportName,
        hasChildren: true,
        expanded: true,
        childrenNum: Object.keys(node.childrenData).length,
        onDrill: () => handleDrill(node.id),
      },
    };

    const childrenMap = {
      [node.id]: Object.entries(node.childrenData).map(([childId, childData]) => ({
        id: childId,
        data: {
          label: childData.label,
          id: childId,
          reportName: node.reportName,
          hasChildren: childData.hasChildren,
          expanded: false,
          childrenNum: childData.childrenNum,
          onDrill: () => handleDrill(childId),
        },
      })),
    };

    const { nodes: treeNodes, edges: treeEdges } = layoutHorizontalTree(rootNode, childrenMap);

    newNodesRef.current = treeNodes;
    edgesRef.current = treeEdges;

    setNodes(treeNodes);
    setEdges(treeEdges);

    if (reactFlowInstance.current) {
      reactFlowInstance.current.fitView({ padding: 0.3, maxZoom: 1.5 });
    }
  }, []);

  const onInit = useCallback((rfi) => {
    reactFlowInstance.current = rfi;
  }, []);

  useEffect(() => {
    const loadRoot = async () => {
      const root = await fetchNode('EC140');
      if (!root) return;
      handleDrill(root.id);
    };
    loadRoot();
  }, [handleDrill]);

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
        fitViewOptions={{ padding: 0.4 }}
        onInit={onInit}
        defaultEdgeOptions={defaultEdgeOptions}
      >
        <Background />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default KPIDiagram;
