// src/components/KPIDiagram.jsx
import ReactFlow, {
    Background,
    Controls,
    useNodesState,
    useEdgesState,
    addEdge,
    Handle,
    Position,
  } from 'react-flow-renderer';

  import { useCallback } from 'react';

  const initialNodes = [
    {
      id: '1',
      type: 'customNode',
      position: { x: 250, y: 5 },
      data: {
        label: 'Time spent listening',
        content: '198.3k mins\n+38.6%',
        onDrill: (id) => alert('Drill into node ' + id),
      },
    },
    {
      id: '2',
      type: 'customNode',
      position: { x: 100, y: 150 },
      data: {
        label: 'Avg. session/week',
        content: '570.13',
        onDrill: (id) => alert('Drill into node ' + id),
      },
    },
    {
      id: '3',
      type: 'customNode',
      position: { x: 400, y: 150 },
      data: {
        label: 'Monthly premium subscriptions',
        content: '246.5k\n+35.8%',
        onDrill: (id) => alert('Drill into node ' + id),
      },
    },
  ];

  const initialEdges = [
    { id: 'e1-2', source: '2', target: '1', animated: true, label: '0.998' },
    { id: 'e1-3', source: '1', target: '3', animated: true, label: '0.999' },
  ];

  const CustomNode = ({ data }) => (
    <div style={{
      border: '1px solid #ccc',
      borderRadius: 8,
      padding: 12,
      background: '#fff',
      minWidth: 180,
      position: 'relative',
    }}>
      <div style={{ fontWeight: 'bold', marginBottom: 4 }}>{data.label}</div>
      <div style={{ fontSize: 12 }}>{data.content}</div>
      <button
        style={{
          position: 'absolute',
          right: 4,
          bottom: 4,
          fontSize: 12,
          background: '#eee',
          borderRadius: 4,
          padding: '2px 6px',
          cursor: 'pointer',
          border: 'none',
        }}
        onClick={() => data.onDrill(data.id)}
      >
        +
      </button>
      <Handle type="target" position={Position.Top} style={{ opacity: 0 }} />
      <Handle type="source" position={Position.Bottom} style={{ opacity: 0 }} />
    </div>
  );

  // src/components/KPIDiagram.jsx (continued)

const nodeTypes = { customNode: CustomNode };

const KPIDiagram = () => {
  const [nodes, , onNodesChange] = useNodesState(initialNodes);
  const [edges, , onEdgesChange] = useEdgesState(initialEdges);

  return (
    <div style={{ height: '80vh', width: '100%' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        fitView
        nodeTypes={nodeTypes}
      >
        <Background />
        <Controls showInteractive={false} />
      </ReactFlow>
    </div>
  );
};

export default KPIDiagram;
