import React, { useEffect, useRef, useState, useCallback } from 'react';
import ReactFlow, {
  Background,
  useNodesState,
  useEdgesState,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import axios from 'axios';

const SPACING_X = 220;
const SPACING_Y = 160;

const initialNodes = [
  {
    id: 'EC140',
    type: 'customNode',
    position: { x: 300, y: 0 },
    data: {
      label: 'Autofinancement',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'EC140',
    },
  },
];

const initialEdges = [];

const CustomNode = ({ data }) => (
  <div style={{
    border: '1px solid #ccc',
    borderRadius: 8,
    padding: 12,
    background: '#fff',
    minWidth: 180,
    position: 'relative',
  }}>
    <div style={{ fontWeight: 'bold', marginBottom: 4 }}>{data.label}</div>
    <button
      style={{
        position: 'absolute',
        right: 4,
        bottom: 4,
        fontSize: 12,
        background: '#eee',
        borderRadius: 4,
        padding: '2px 6px',
        cursor: 'pointer',
        border: 'none',
      }}
      onClick={() => data.onDrill(data.id)}
    >
      +
    </button>
    <Handle type="target" position={Position.Top} style={{ opacity: 0 }} />
    <Handle type="source" position={Position.Bottom} style={{ opacity: 0 }} />
  </div>
);

const nodeTypes = {
  customNode: CustomNode,
};

const KPIDiagram = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [level, setLevel] = useState(1);

  const newNodesRef = useRef([...initialNodes]);
  const edgesRef = useRef([...initialEdges]);

  const fetchNodes = useCallback(async () => {
    try {
      const response = await axios.post("http://localhost:8000/api/getData", { level });
      const nodesData = response.data.nodesData;

      nodesData.forEach((nodeGroup, groupIndex) => {
        const parentId = nodeGroup.parentId;
        const parentName = nodeGroup.parentName;

        // Add parent node if not already added
        if (!newNodesRef.current.find(n => n.id === parentId)) {
          const parentX = SPACING_X * groupIndex;
          const parentY = SPACING_Y * (level - 1);

          newNodesRef.current.push({
            id: parentId,
            type: 'customNode',
            position: { x: parentX, y: parentY },
            data: {
              label: parentName,
              id: parentId,
              onDrill: () => handleDrill(parentId, nodeGroup.childrenData, parentX, parentY),
            },
          });
        }

        // Add children and edges
        Object.entries(nodeGroup.childrenData).forEach(([childId, childLabel], childIndex) => {
          const childX = SPACING_X * childIndex;
          const childY = SPACING_Y * level;

          // Avoid duplicate child nodes
          if (!newNodesRef.current.find(n => n.id === childId)) {
            newNodesRef.current.push({
              id: childId,
              type: 'customNode',
              position: { x: childX, y: childY },
              data: {
                label: childLabel,
                id: childId,
                onDrill: () => alert('Drill into node ' + childId),
              },
            });
          }

          // Avoid duplicate edges
          const edgeId = `e-${parentId}-${childId}`;
          if (!edgesRef.current.find(e => e.id === edgeId)) {
            edgesRef.current.push({
              id: edgeId,
              source: parentId,
              target: childId,
              animated: true,
            });
          }
        });
      });

      // Deduplicate nodes and edges
      const uniqueNodes = Array.from(new Map(newNodesRef.current.map(n => [n.id, n])).values());
      const uniqueEdges = Array.from(new Map(edgesRef.current.map(e => [e.id, e])).values());

      setNodes(uniqueNodes);
      setEdges(uniqueEdges);
    } catch (error) {
      console.error('Fetch failed', error);
    }
  }, [level]);

  const handleDrill = (parentId, childrenData, parentX, parentY) => {
    Object.entries(childrenData).forEach(([childId, childLabel], childIndex) => {
      const childX = parentX + SPACING_X * (childIndex - 1);
      const childY = parentY + SPACING_Y;

      if (!newNodesRef.current.find(n => n.id === childId)) {
        newNodesRef.current.push({
          id: childId,
          type: 'customNode',
          position: { x: childX, y: childY },
          data: {
            label: childLabel,
            id: childId,
            onDrill: () => alert("Drill into node " + childId),
          },
        });

        const edgeId = `e-${parentId}-${childId}`;
        edgesRef.current.push({
          id: edgeId,
          source: parentId,
          target: childId,
          animated: true,
        });
      }
    });

    setLevel(prev => prev + 1);
  };

  useEffect(() => {
    fetchNodes();
  }, [level, fetchNodes]);

  return (
    <div style={{ height: '80vh', width: '100%' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        nodeTypes={nodeTypes}
        fitView
        nodesDraggable={false}
      >
        <Background />
      </ReactFlow>
      <div style={{ marginTop: 8, paddingLeft: 16 }}>
        <button onClick={() => setLevel(prev => prev + 1)}>Load Next Level</button>
      </div>
    </div>
  );
};

export default KPIDiagram;
