// src/components/KPIDiagram.jsx
import React, {useEffect,useRef, useMemo, useState, useCallback, Children} from 'react';
import ReactFlow, {
  Background,
  useNodesState,
  useEdgesState,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import axios from 'axios'
const initialNodes = [
  {
    id: 'EC140',
    type: 'customNode',
    position: { x: 250, y: 5 },
    data: {
      label: 'Autofinancement',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'EC140',
    },
  },
  {
    id: 'R07',
    type: 'customNode',
    position: { x: 100, y: 150 },
    data: {
      label: 'Capacité d\'autofinancement',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'R07',
    },
  },
  {
    id: 'EC047',
    type: 'customNode',
    position: { x: 400, y: 150 },
    data: {
      label: 'Dividendes',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'EC047',
    },
  },
];

const initialEdges = [
  { id: 'e1-2', source: 'EC140', target: 'R07', animated: true },
  { id: 'e1-3', source: 'EC140', target: 'EC047', animated: true },
];

const CustomNode = ({ data }) => (
  <div style={{
    border: '1px solid #ccc',
    borderRadius: 8,
    padding: 12,
    background: '#fff',
    minWidth: 180,
    position: 'relative',
  }}>
    <div style={{ fontWeight: 'bold', marginBottom: 4 }}>{data.label}</div>
    <button
      style={{
        position: 'absolute',
        right: 4,
        bottom: 4,
        fontSize: 12,
        background: '#eee',
        borderRadius: 4,
        padding: '2px 6px',
        cursor: 'pointer',
        border: 'none',
      }}
      onClick={() => data.onDrill(data.id)}
    >
      +
    </button>
    <Handle type="target" position={Position.Top} style={{ opacity: 0 }} />
    <Handle type="source" position={Position.Bottom} style={{ opacity: 0 }} />
  </div>
);

const nodeTypes = {
  customNode: CustomNode,
};

const KPIDiagram = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [level, SetLevel] = useState(1);
  const [position, setPosition] = useState({x : 150, y : 5});
  // const [nodesData, setNodesData] = useState(null);
  const newNodesRef = useRef([]);
  const edgesRef = useRef([]);

  useEffect(()=>{
    const fetch = async ()=>{
      try{
          const response = await axios.post("http://localhost:8000/api/getData",{ level :level});
          const nodesData  = response.data.nodesData;

          const levelMap = {}; // { levelNumber: count }

nodesData.forEach(node => {
  const parentId = node.parentId;
  const nodeLevel = Number(node.level) || 0;

  // Count how many nodes are at this level
  if (!levelMap[nodeLevel]) levelMap[nodeLevel] = 0;
  const x = 200 + levelMap[nodeLevel] * 250;
  const y = 100 + nodeLevel * 180;
  levelMap[nodeLevel] += 1;

  // Add parent node
  newNodesRef.current.push({
    id: parentId,
    type: "customNode",
    position: { x, y },
    data: {
      label: node.parentName,
      id: parentId,
      onDrill: () => {
        SetLevel(prev => prev + 1);
      }
    }
  });

  Object.entries(node.childrenData).forEach(([childId, childLabel]) => {
    // Add edge
    edgesRef.current.push({
      id: `e-${parentId}-${childId}`,
      source: parentId,
      target: childId,
      animated: true
    });

    // Add child node (at next level)
    const childLevel = nodeLevel + 1;
    if (!levelMap[childLevel]) levelMap[childLevel] = 0;
    const cx = 200 + levelMap[childLevel] * 250;
    const cy = 100 + childLevel * 180;
    levelMap[childLevel] += 1;

    newNodesRef.current.push({
      id: childId,
      type: "customNode",
      position: { x: cx, y: cy },
      data: {
        label: childLabel,
        id: childId,
        onDrill: () => {
          SetLevel(prev => prev + 1);
        }
      }
    });
  });
});

          const uniqueNodes = Array.from(
            new Map(newNodesRef.current.map(node => [node.id, node])).values()
          );
          const uniqueEdges = Array.from(
            new Map(edgesRef.current.map(node => [node.id, node])).values()
          );
          setNodes(uniqueNodes);
          setEdges(uniqueEdges)
          console.log(uniqueEdges);
          setPosition((prev) => ({
            ...prev,
            xy : prev.y + 100,
          }))
      }
      catch(error)
      {
        console.error(error);
      }
    }

    fetch();
  }, [level])

  return (
    <div style={{ height: '80vh', width: '100%' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        nodeTypes={nodeTypes}
        onNodesChange={onNodesChange} 
        onEdgesChange={onEdgesChange}
        fitView
        panOnDrag={false}
        zoomOnScroll={true}
        nodesDraggable={true}
        nodesConnectable={true}
      >
        {/* <Background /> */}
      </ReactFlow>
    <button onClick={()=>{SetLevel(prev => prev + 1)}}>press</button>
    </div>
  );
};

export default KPIDiagram;
