import React, { useCallback, useEffect, useMemo, useRef, useState , memo} from 'react';
import ReactFlow, {
  Background,
  Controls,
  useNodesState,
  useReactFlow, 
  useEdgesState,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import axios from 'axios';
import ReactDOM from 'react-dom';

// CustomNode component
// import { motion, AnimatePresence } from 'framer-motion';
import { motion } from 'framer-motion';
import './KPIDiagram.css'

// const HoverNode = ({ data }) => (
//   <div className="tooltip-box ">
//    <div className="font-semibold text-amber-400 mb-1">nodeID: {data.id}</div>
//     <div className="text-amber-300 mb-1">reportName: {data.reportName}</div>
//     <div className="text-white">Label: {data.label}</div>
//     <div className="text-gray-300">• Detail 1</div>
//     <div className="text-gray-300">• Detail 2</div>
//     <div className="text-gray-300">• Detail 3</div>
//   </div>
// );



const CollapsibleField = ({ label, value }) => {
  const [open, setOpen] = useState(false)
  const isObject = typeof value === "object" && value !== null

  if (value === undefined) return null

  return (
    <div className="mb-2 border-b border-gray-100 pb-2 last:border-b-0">
      <div
        className="flex items-center justify-between cursor-pointer text-xs font-semibold text-gray-600 hover:text-blue-600 transition-colors"
        onClick={() => isObject && setOpen(!open)}
      >
        <span>{label}:</span>
        {isObject && (
          <span className="text-[10px] bg-gray-200 hover:bg-gray-300 rounded px-2 py-1 transition-colors">
            {open ? "Hide" : "Show"}
          </span>
        )}
      </div>

      {!isObject && (
        <div className="text-xs text-gray-800 break-words mt-1 max-h-16 overflow-y-auto">
          {String(value)}
        </div>
      )}

      {isObject && open && (
        <div className="mt-2">
