import React, { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import ReactFlow, {
  Background,
  Controls,
  useNodesState,
  useEdgesState,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import axios from 'axios';

// Custom node with drill button

const CustomNode = ({ data }) => {
  const [fadeIn, setFadeIn] = useState(false);
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    setFadeIn(true);
  }, []);

  // When parent signals collapse, data.collapsing === true triggers fade-out
  useEffect(() => {
    if (data.collapsing) {
      setFadeOut(true);
      setFadeIn(false);
    }
  }, [data.collapsing]);

  // Inform parent after fade out animation ends to remove node
  const onAnimationEnd = () => {
    if (fadeOut && data.onCollapseEnd) {
      data.onCollapseEnd(data.id);
    }
  };

  return (
    <div
      className={fadeIn ? (fadeOut ? 'fade-out' : 'fade-in') : ''}
      onAnimationEnd={onAnimationEnd}
      style={{
        border: '1px solid #ccc',
        padding: '12px 16px',
        borderRadius: 12,
        background: '#fff',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        minWidth: 180,
        maxWidth: 200,
        textAlign: 'center',
        fontSize: 14,
        fontWeight: 500,
        position: 'relative',
        transition: 'box-shadow 0.3s ease',
      }}
    >
      <Handle type="target" position={Position.Top} id="a" />
      <div style={{ marginBottom: 8 }}>{data.label}</div>
      {data.hasChildren && (
        <button
          onClick={data.onDrill}
          style={{
            padding: '4px 8px',
            fontSize: 12,
            borderRadius: 6,
            border: 'none',
            background: '#007bff',
            color: 'white',
            cursor: 'pointer',
          }}
        >
          {data.expanded ? '➖' : '➕'}
        </button>
      )}
      <Handle type="source" position={Position.Bottom} id="a" />
    </div>
  );
};


const KPIDiagram = () => {
  const [level, setLevel] = useState(0);
  const newNodesRef = useRef([]);
  const edgesRef = useRef([]);
  const expandedNodesRef = useRef(new Set());

  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const nodeTypes = useMemo(() => ({ customNode: CustomNode }), []);

  const fetchNode = async (nodeId) => {
    try {
      const res = await axios.get(`http://localhost:8000/api/node/${nodeId}`);
      return res.data.node;
    } catch (error) {
      console.error('Error fetching node:', error);
      return null;
    }
  };

  // Recursive collapse
  const removeDescendants = (parentId) => {
    const toRemove = new Set();
    const stack = [parentId];

    while (stack.length > 0) {
      const current = stack.pop();

      const children = edgesRef.current
        .filter((e) => e.source === current)
        .map((e) => e.target);

      children.forEach((childId) => {
        stack.push(childId);
        toRemove.add(childId);
      });
    }

    // Remove descendant nodes
    newNodesRef.current = newNodesRef.current.filter((n) => !toRemove.has(n.id));

    // Remove edges related to those nodes
    edgesRef.current = edgesRef.current.filter(
      (e) => !toRemove.has(e.target) && !toRemove.has(e.source)
    );

    // Update expanded set
    for (const id of toRemove) expandedNodesRef.current.delete(id);
    expandedNodesRef.current.delete(parentId);

    // Mark parent as collapsed
    newNodesRef.current = newNodesRef.current.map((n) =>
      n.id === parentId ? { ...n, data: { ...n.data, expanded: false } } : n
    );
  };

  const handleDrill = useCallback(
    async (nodeId) => {
      const isExpanded = expandedNodesRef.current.has(nodeId);

      if (isExpanded) {
        removeDescendants(nodeId);
        setNodes([...newNodesRef.current]);
        setEdges([...edgesRef.current]);
        return;
      }

      const node = await fetchNode(nodeId);
      if (!node || !node.childrenData) return;

      const parentNode = newNodesRef.current.find((n) => n.id === nodeId);
      const nextLevel = parseInt(node.level) + 1 || level + 1;
      const centerX = parentNode?.position?.x || 0;

      const entries = Object.entries(node.childrenData);
      const totalChildren = entries.length;
      const baseY = 100 + nextLevel * 180;
      const spacing = Math.max(200, 1200 / totalChildren);
      const startX = centerX - ((totalChildren - 1) * spacing) / 2;

      entries.forEach(([childId, childLabel], index) => {
        const childX = startX + index * spacing;

        edgesRef.current.push({
          id: `e-${nodeId}-${childId}`,
          source: nodeId,
          sourceHandle: 'a',
          target: childId,
          targetHandle: 'a',
          animated: true,
        });

        newNodesRef.current.push({
          id: childId,
          type: 'customNode',
          position: { x: childX, y: baseY },
          data: {
            label: childLabel.label,
            id: childId,
            hasChildren: childLabel.hasChildren,
            expanded: false,
            onDrill: () => handleDrill(childId),
          },
        });
      });

      // Remove duplicates
      const uniqueNodes = Array.from(
        new Map(newNodesRef.current.map((n) => [n.id, n])).values()
      );
      const uniqueEdges = Array.from(
        new Map(edgesRef.current.map((e) => [e.id, e])).values()
      );

      newNodesRef.current = uniqueNodes;
      edgesRef.current = uniqueEdges;
      expandedNodesRef.current.add(nodeId);

      // Mark parent as expanded
      newNodesRef.current = newNodesRef.current.map((n) =>
        n.id === nodeId ? { ...n, data: { ...n.data, expanded: true } } : n
      );

      setNodes([...uniqueNodes]);
      setEdges([...uniqueEdges]);
      setLevel(nextLevel);
    },
    [level]
  );

  useEffect(() => {
    const loadRoot = async () => {
      const root = await fetchNode('EC140');
      if (!root) return;

      const rootId = root.parentId || 'EC140';
      newNodesRef.current.push({
        id: rootId,
        type: 'customNode',
        position: { x: 500, y: 50 },
        data: {
          label: root.parentName || 'Root Node',
          id: rootId,
          hasChildren: true,
          expanded: false,
          onDrill: () => handleDrill(rootId),
        },
      });

      setNodes([...newNodesRef.current]);
    };

    loadRoot();
  }, [handleDrill]);

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
        fitViewOptions={{ padding: 0.4 }}
      >
        <Background />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default KPIDiagram;
