// src/components/KPIDiagram.jsx
import React from 'react';
import ReactFlow, {
  Background,
  useNodesState,
  useEdgesState,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';



const initialNodes = [
  {
    id: 'EC140',
    type: 'customNode',
    position: { x: 250, y: 5 },
    data: {
      label: 'Autofinancement',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'EC140',
    },
  },
  {
    id: 'R07',
    type: 'customNode',
    position: { x: 100, y: 150 },
    data: {
      label: 'Capacité d\'autofinancement',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'R07',
    },
  },
  {
    id: 'EC047',
    type: 'customNode',
    position: { x: 400, y: 150 },
    data: {
      label: 'Dividendes',
      onDrill: (id) => alert('Drill into node ' + id),
      id: 'EC047',
    },
  },
];

const initialEdges = [
  { id: 'e1-2', source: 'EC140', target: 'R07', animated: true },
  { id: 'e1-3', source: 'EC140', target: 'EC047', animated: true },
];

const CustomNode = ({ data }) => (
  <div style={{
    border: '1px solid #ccc',
    borderRadius: 8,
    padding: 12,
    background: '#fff',
    minWidth: 180,
    position: 'relative',
  }}>
    <div style={{ fontWeight: 'bold', marginBottom: 4 }}>{data.label}</div>
    <button
      style={{
        position: 'absolute',
        right: 4,
        bottom: 4,
        fontSize: 12,
        background: '#eee',
        borderRadius: 4,
        padding: '2px 6px',
        cursor: 'pointer',
        border: 'none',
      }}
      onClick={() => data.onDrill(data.id)}
    >
      +
    </button>
    <Handle type="target" position={Position.Top} style={{ opacity: 0 }} />
    <Handle type="source" position={Position.Bottom} style={{ opacity: 0 }} />
  </div>
);

const nodeTypes = {
  customNode: CustomNode,
};

const KPIDiagram = () => {
  const [nodes, , onNodesChange] = useNodesState(initialNodes);
  const [edges, , onEdgesChange] = useEdgesState(initialEdges);



  return (
    <div style={{ height: '80vh', width: '100%' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        nodeTypes={nodeTypes}
        fitView
        // panOnDrag={false}
        // zoomOnScroll={false}
        nodesDraggable={false}
        // nodesConnectable={false}
      >
        <Background />
      </ReactFlow>
    </div>
  );
};

export default KPIDiagram;
